FROM MATERIALS


        MATERIAL_ID = #{materialId,jdbcType=INTEGER}


        AND MATERIAL_NAME LIKE '%'||#{materialName,jdbcType=VARCHAR}||'%'


        AND URI = #{uri,jdbcType=VARCHAR}


        AND ROWNUM < #{page.pageSize,jdbcType=INTEGER}+#{page.first,jdbcType=INTEGER}+1



    MINUS
    SELECT MATERIAL_ID,MATERIAL_NAME,URI
    FROM MATERIALS


            MATERIAL_ID = #{materialId,jdbcType=INTEGER}


            AND MATERIAL_NAME LIKE '%'||#{materialName,jdbcType=VARCHAR}||'%'


            AND URI = #{uri,jdbcType=VARCHAR}


            AND ROWNUM < #{page.first,jdbcType=INTEGER}+1

